	copy(t2,timertick);
	sub(dt,t2,t1);
	if(dt GT 100,
		copy(t1,t2);
		sub(dt,t1,t0);
		div(dt,1000);
		Math.max(dt,1);
		mul(dt,%1);
		vr_setup_adjust(get(dt));
	);
);

