		if(i_fov == hs.fov AND i_dist == hs.dist AND i_vig == hs.vig AND i_overlap == hs.overlap AND i_dist2 == hs.dist2 AND i_ca == hs.ca,
			copy(i_hsindex, vrheadj);
			copy(i_headset, hs.caption);
		);
	);

	if(%1 GT 0,

		add(i_hsindex, 1);
