		if(i_hsindex LT 0, sub(i_hsindex,i_hscount,1));
	);

	copy(hs, vrheadsets.headset[get(i_hsindex)]);
	copy(i_headset, hs.caption);
	copy(i_fov, hs.fov);
	copy(i_dist, hs.dist);
	copy(i_dist2, hs.dist2);
	copy(i_vig, hs.vig);
